<?php

namespace Servier\Admin;

class AdminFooter
{
    public function __construct()
    {
        if (is_admin()) {
            add_action('admin_footer_text', [$this, 'footer_html']);
        }
    }

    // Change credits
    public static function footer_html()
    {
        _e('<span id="footer-thankyou"><a href="http://groupe361.com" target="_blank"><img src=\'data:image/png;base64,\' /></a></span>', 'custom-admin-footer');
    }
}
